    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFSoftShadowMap;
    mount.appendChild(renderer.domElement);

    // Environment (simple HDR-like via large soft light + rim)
    const hemi = new THREE.HemisphereLight(0xffffff, 0x444466, 0.6);
    scene.add(hemi);

    const dir = new THREE.DirectionalLight(0xffffff, 0.9);
    dir.position.set(5, 8, 3);
    dir.castShadow = true;
    dir.shadow.mapSize.set(2048, 2048);
    dir.shadow.camera.left = -5;
    dir.shadow.camera.right = 5;
    dir.shadow.camera.top = 5;
    dir.shadow.camera.bottom = -5;
    scene.add(dir);

    // ground (receives shadow)
    const groundMat = new THREE.MeshStandardMaterial({ color: 0xe6f2f9, metalness: 0, roughness: 1 });
    const ground = new THREE.Mesh(new THREE.PlaneGeometry(20, 20), groundMat);
    ground.rotation.x = -Math.PI / 2;
    ground.position.y = -1.15;
    ground.receiveShadow = true;
    scene.add(ground);

    // Character group
    const char = new THREE.Group();
    scene.add(char);

    // Materials (PBR-ish)
    const bodyMat = new THREE.MeshStandardMaterial({ color: 0x00bcd4, metalness: 0.1, roughness: 0.55 });
    const skinMat = new THREE.MeshStandardMaterial({ color: 0xffe0f0, metalness: 0, roughness: 0.7 });
    const hatMat = new THREE.MeshStandardMaterial({ color: 0xb39ddb, metalness: 0.05, roughness: 0.6 });
    const pantMat = new THREE.MeshStandardMaterial({ color: 0x26418f, metalness: 0.05, roughness: 0.6 });
    const bootMat = new THREE.MeshStandardMaterial({ color: 0x00bcd4, metalness: 0.1, roughness: 0.4 });
    const blackMat = new THREE.MeshBasicMaterial({ color: 0x111111 });

    // --- Build smooth character parts (rounded shapes) ---

    // torso as capsule (approximate smooth jelly)
    const torsoGeo = new THREE.CapsuleGeometry(0.36, 0.78, 8, 24);
    const torso = new THREE.Mesh(torsoGeo, bodyMat);
    torso.position.y = 0.45;
    torso.castShadow = true;
    torso.receiveShadow = true;
    char.add(torso);

    // head sphere
    const head = new THREE.Mesh(new THREE.SphereGeometry(0.32, 32, 24), skinMat);
    head.position.y = 1.4;
    head.castShadow = true;
    char.add(head);

    // hat: brim + top
    const hatBrim = new THREE.Mesh(new THREE.TorusGeometry(0.36, 0.045, 16, 60), hatMat);
    hatBrim.rotation.x = Math.PI / 2;
    hatBrim.position.y = 1.65;
    char.add(hatBrim);

    const hatTop = new THREE.Mesh(new THREE.CylinderGeometry(0.28, 0.28, 0.24, 32), hatMat);
    hatTop.position.y = 1.78;
    char.add(hatTop);

    // hair "curtain" front (a smooth extrude: approximated by lathed geometry)
    const hairShape = new THREE.Shape();
    hairShape.moveTo(-0.28, 0);
    hairShape.quadraticCurveTo(-0.15, -0.7, 0, -0.75);
    hairShape.quadraticCurveTo(0.15, -0.7, 0.28, 0);
    const extrudeSettings = { depth: 0.12, bevelEnabled: false };
    const hairGeo = new THREE.ExtrudeGeometry(hairShape, extrudeSettings);
    hairGeo.rotateX(-Math.PI / 2);
    hairGeo.translate(0, 1.35, 0.04);
    const hairMat = new THREE.MeshStandardMaterial({ color: 0x00bcd4, metalness: 0.05, roughness: 0.5 });
    const hair = new THREE.Mesh(hairGeo, hairMat);
    hair.castShadow = true;
    char.add(hair);

