    const eyeL = new THREE.Mesh(new THREE.SphereGeometry(0.04, 12, 12), blackMat);
    eyeL.position.set(-0.08, 1.47, 0.28);
    const eyeR = eyeL.clone();
    eyeR.position.x = 0.08;
    char.add(eyeL, eyeR);

