    function addLash(x) {
      const lashGeo = new THREE.PlaneGeometry(0.06, 0.02);
      const lash = new THREE.Mesh(lashGeo, blackMat);
      lash.position.set(x, 1.52, 0.29);
      lash.rotation.y = 0.0;
      lash.rotation.x = -0.3;
      char.add(lash);
