    // mouth (small arc made with curve points -> Line)
    const mouthPts = [];
    for (let i = 0; i <= 10; i++) {
      const a = -0.4 + (i / 10) * 0.8;
      mouthPts.push(new THREE.Vector3(Math.sin(a) * 0.06, 1.37 - Math.abs(a) * 0.01, 0.28));
    }
    const mouthGeo = new THREE.BufferGeometry().setFromPoints(mouthPts);
    const mouth = new THREE.Line(mouthGeo, new THREE.LineBasicMaterial({ color: 0x111111, linewidth: 2 }));
    char.add(mouth);

    // arms (capsules)
    const armGeo = new THREE.CapsuleGeometry(0.08, 0.46, 6, 12);
    const leftArm = new THREE.Mesh(armGeo, bodyMat);
    leftArm.position.set(-0.5, 0.95, 0);
    leftArm.rotation.z = 0.25;
    leftArm.castShadow = true;
    const rightArm = leftArm.clone();
    rightArm.position.x = 0.5;
    rightArm.rotation.z = -0.25;
    char.add(leftArm, rightArm);

    // pants + legs + boots
    const pants = new THREE.Mesh(new THREE.CylinderGeometry(0.22, 0.22, 0.34, 24), pantMat);
    pants.position.y = 0.0;
    pants.castShadow = true;
    char.add(pants);

    function makeLeg(x) {
      const leg = new THREE.Mesh(new THREE.CylinderGeometry(0.10, 0.10, 0.36, 12), pantMat);
      leg.position.set(x, -0.18, 0);
      leg.castShadow = true;

      const boot = new THREE.Mesh(new THREE.CapsuleGeometry(0.13, 0.06, 6, 12), bootMat);
      boot.position.set(x, -0.46, 0.06);
      boot.castShadow = true;

      const trim = new THREE.Mesh(new THREE.TorusGeometry(0.14, 0.02, 8, 32), hatMat);
      trim.position.set(x, -0.30, 0.06);
      trim.rotation.x = Math.PI / 2;

      return [leg, boot, trim];
    }

    const [legL, bootL, trimL] = makeLeg(-0.13);
    const [legR, bootR, trimR] = makeLeg(0.13);
    char.add(legL, bootL, trimL, legR, bootR, trimR);

    // belt (torus) + small pouch (sphere)
    const belt = new THREE.Mesh(new THREE.TorusGeometry(0.28, 0.03, 12, 32), new THREE.MeshStandardMaterial({ color: 0x00bcd4 }));
    belt.rotation.x = Math.PI / 2;
    belt.position.y = 0.32;
    char.add(belt);

    const pouch = new THREE.Mesh(new THREE.SphereGeometry(0.06, 12, 12), hatMat);
    pouch.position.set(0.18, 0.32, 0.08);
    char.add(pouch);

    // --- end character construction ---

    // subtle rim light (fake) via point lights
    const rim = new THREE.PointLight(0xb6d7ff, 0.25, 6);
    rim.position.set(-2, 2, -1);
    scene.add(rim);

    // Animation (idle bob + gentle arm motion)
    const clock = new THREE.Clock();
    let req;
    function animate() {
      const t = clock.getElapsedTime();
      char.position.y = Math.sin(t * 1.8) * 0.04; // bob
      char.rotation.y = Math.sin(t * 0.5) * 0.08; // sway
      leftArm.rotation.x = Math.sin(t * 2) * 0.18 + 0.25;
      rightArm.rotation.x = Math.sin(t * 2 + Math.PI) * 0.18 - 0.25;
      renderer.render(scene, camera);
      req = requestAnimationFrame(animate);
    }
    animate();

    // handle resize
    function onResize() {
      const w = mount.clientWidth || 480;
      const h = mount.clientHeight || 480;
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
      renderer.setSize(w, h);
    }
    window.addEventListener("resize", onResize);

    // --- Export GLB helper (exposed via window for demo, real export uses GLTFExporter below) ---
    // cleanup on unmount
    return () => {
      cancelAnimationFrame(req);
      window.removeEventListener("resize", onResize);
      renderer.dispose();
      // dispose geometries & materials
      scene.traverse((o) => {
        if (o.isMesh) {
          if (o.geometry) o.geometry.dispose();
          if (o.material) {
            if (Array.isArray(o.material)) o.material.forEach(m => m.dispose());
            else o.material.dispose();
          }
        }
      });
      if (mount && renderer.domElement) mount.removeChild(renderer.domElement);
    };
  }, []);

  // Exporter function: export assembled character group into glb and force download
  const handleExport = () => {
    if (!mountRef.current) return;
    setExporting(true);
    // We need to reconstruct same scene graph as in effect — easiest: serialize the current canvas scene
    // Approach: Find scene from renderer via dom element (hack: store scene object onto dom or global)
    // But we didn't keep references outside effect; so re-create character quickly and export same geometry.
    // For reliability we re-create a minimal character object identical to runtime (same geometry & materials).
    const exporter = new GLTFExporter();

    // Helper: build the same character group used above (but without lights/renderer)
    const buildExportChar = () => {
      const group = new THREE.Group();

      const bodyMat = new THREE.MeshStandardMaterial({ color: 0x00bcd4 });
      const skinMat = new THREE.MeshStandardMaterial({ color: 0xffe0f0 });
      const hatMat = new THREE.MeshStandardMaterial({ color: 0xb39ddb });
      const pantMat = new THREE.MeshStandardMaterial({ color: 0x26418f });
      const bootMat = new THREE.MeshStandardMaterial({ color: 0x00bcd4 });
      const blackMat = new THREE.MeshBasicMaterial({ color: 0x111111 });

      const torso = new THREE.Mesh(new THREE.CapsuleGeometry(0.36, 0.78, 8, 24), bodyMat);
      torso.position.y = 0.45;
      group.add(torso);

      const head = new THREE.Mesh(new THREE.SphereGeometry(0.32, 32, 24), skinMat);
      head.position.y = 1.4;
      group.add(head);

      const hatBrim = new THREE.Mesh(new THREE.TorusGeometry(0.36, 0.045, 16, 60), hatMat);
      hatBrim.rotation.x = Math.PI / 2;
      hatBrim.position.y = 1.65;
      group.add(hatBrim);

      const hatTop = new THREE.Mesh(new THREE.CylinderGeometry(0.28, 0.28, 0.24, 32), hatMat);
      hatTop.position.y = 1.78;
      group.add(hatTop);

      // Fix: Correct hair shape construction
      const hairShape = new THREE.Shape();
      hairShape.moveTo(-0.28, 0);
      hairShape.quadraticCurveTo(-0.15, -0.7, 0, -0.75);
      hairShape.quadraticCurveTo(0.15, -0.7, 0.28, 0);
      const extrudeSettings = { depth: 0.12, bevelEnabled: false };
      const hairGeo = new THREE.ExtrudeGeometry(hairShape, extrudeSettings);
      hairGeo.rotateX(-Math.PI / 2);
      hairGeo.translate(0, 1.35, 0.04);
      const hair = new THREE.Mesh(hairGeo, bodyMat);
      group.add(hair);

      const eyeL = new THREE.Mesh(new THREE.SphereGeometry(0.04, 12, 12), blackMat);
      eyeL.position.set(-0.08, 1.47, 0.28);
      const eyeR = eyeL.clone();
      eyeR.position.x = 0.08;
      group.add(eyeL, eyeR);

      // mouth: replace Line with thin mesh for better GLTF compatibility
      const mouthGeo = new THREE.TubeGeometry(
        new THREE.CatmullRomCurve3(mouthPts),
        10,
        0.005,
        8,
        false
      );
      const mouth = new THREE.Mesh(mouthGeo, blackMat);
      group.add(mouth);

      // arms, legs, boots similar to runtime
      const armGeo = new THREE.CapsuleGeometry(0.08, 0.46, 6, 12);
      const leftArm = new THREE.Mesh(armGeo, bodyMat); leftArm.position.set(-0.5, 0.95, 0); leftArm.rotation.z = 0.25;
      const rightArm = leftArm.clone(); rightArm.position.x = 0.5; rightArm.rotation.z = -0.25;
      group.add(leftArm, rightArm);

      const pants = new THREE.Mesh(new THREE.CylinderGeometry(0.22, 0.22, 0.34, 24), pantMat); pants.position.y = 0.0; group.add(pants);

      const makeLeg = (x) => {
        const leg = new THREE.Mesh(new THREE.CylinderGeometry(0.10, 0.10, 0.36, 12), pantMat);
        leg.position.set(x, -0.18, 0);
        const boot = new THREE.Mesh(new THREE.CapsuleGeometry(0.13, 0.06, 6, 12), bootMat);
        boot.position.set(x, -0.46, 0.06);
        const trim = new THREE.Mesh(new THREE.TorusGeometry(0.14, 0.02, 8, 32), hatMat);
        trim.position.set(x, -0.30, 0.06); trim.rotation.x = Math.PI / 2;
        return [leg, boot, trim];
      };
      const [l1,l2,l3] = makeLeg(-0.13); const [r1,r2,r3] = makeLeg(0.13);
      group.add(l1,l2,l3,r1,r2,r3);

      const belt = new THREE.Mesh(new THREE.TorusGeometry(0.28, 0.03, 12, 32), bodyMat); belt.rotation.x = Math.PI/2; belt.position.y = 0.32;
      group.add(belt);
      const pouch = new THREE.Mesh(new THREE.SphereGeometry(0.06, 12, 12), hatMat); pouch.position.set(0.18, 0.32, 0.08);
      group.add(pouch);

      return group;
    };

    const charObj = buildExportChar();

    // Export to glb
    exporter.parse(
      charObj,
      (glb) => {
        // glb is ArrayBuffer when options.binary = true
        const blob = new Blob([glb], { type: "model/gltf-binary" });
        const url = URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = "character.glb";
        a.click();
        URL.revokeObjectURL(url);
        setExporting(false);
      },
      (error) => {
        console.error("GLTF export error:", error);
        setExporting(false);
      },
      { binary: true }
    );
  };

  return (
    <div style={{ display: "flex", gap: 12, alignItems: "flex-start" }}>
      <div
        ref={mountRef}
        style={{
          width: 360,
          height: 480,
          borderRadius: 16,
          boxShadow: "0 8px 30px rgba(0,0,0,0.12)",
          overflow: "hidden",
          background: "#eaf7ff"
        }}
      />
      <div style={{ display: "flex", flexDirection: "column", gap: 8 }}>
        <button onClick={handleExport} disabled={exporting} style={{ padding: "8px 12px", borderRadius: 8 }}>
          {exporting ? "Exporting..." : "Export GLB"}
        </button>
        <div style={{ maxWidth: 220, fontSize: 13, color: "#333" }}>
          <b>Tips:</b>
          <ul style={{ marginTop: 6 }}>
            <li>Click "Export GLB" to download the model file <code>character.glb</code>.</li>
            <li>Drop <code>character.glb</code> into <code>public/models/</code> and load with useGLTF in React Three Fiber.</li>
            <li>If you want rigged skeleton for animations, tell me — I can produce a skinned export next.</li>
          </ul>
        </div>
      </div>
    </div>
  );
}
