import React from "react";
import "../styles/Home.css";
import ThreePerson from "../components/ThreePerson";

function HeroSection() {
  return (
    <section className="hero-section">
      <div className="container">
        <div className="hero-content">
          {/* Bên trái: chữ */}
          <div className="hero-text">
            <h1>
              Xin chào, tôi là <br />
              <span className="highlight">Trần Khánh Linh</span>
            </h1>
            <p>
              <b>Software Engineer in Progress</b> — tôi theo đuổi đam mê sáng tạo
              và công nghệ, với mong muốn mang lại trải nghiệm số tốt nhất cho
              người dùng.
            </p>
            <a href="/about" className="btn-main">
              Về tôi
            </a>
          </div>

          {/* Bên phải: 3D model */}
          <div className="hero-image" role="img" aria-label="3D person waving">
            <ThreePerson />
          </div>
        </div>
      </div>
    </section>
  );
}

export default HeroSection;
