import React, { useRef, useEffect } from "react";
import * as THREE from "three";

export default function ThreePerson() {
  const mountRef = useRef(null);

  useEffect(() => {
    const mount = mountRef.current;
    const width = mount.clientWidth || 320;
    const height = mount.clientHeight || 320;

    // Scene & Camera
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xb3e5fc);

    const camera = new THREE.PerspectiveCamera(45, width / height, 0.1, 100);
    camera.position.set(0, 1.2, 3.2);
    camera.lookAt(0, 0.9, 0);

    // Renderer
    const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: false });
    renderer.setSize(width, height);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    renderer.outputColorSpace = THREE.SRGBColorSpace;
    mount.appendChild(renderer.domElement);

    // Lights
    scene.add(new THREE.AmbientLight(0xffffff, 0.7));
    const dir = new THREE.DirectionalLight(0xffffff, 0.7);
    dir.position.set(2, 4, 2);
    scene.add(dir);

    // Ground
    const groundMat = new THREE.MeshLambertMaterial({ color: 0xfffde7 });
    const ground = new THREE.Mesh(new THREE.CircleGeometry(2.2, 40), groundMat);
    ground.rotation.x = -Math.PI / 2;
    ground.position.y = 0;
    scene.add(ground);

    // Palm trees (simple stylized)
    function addPalm(x, z, h = 1.2) {
      // Trunk
      const trunkMat = new THREE.MeshLambertMaterial({ color: 0xffcc80 });
      const trunk = new THREE.Mesh(new THREE.CylinderGeometry(0.05, 0.08, h, 8), trunkMat);
      trunk.position.set(x, h / 2 + 0.01, z);
      scene.add(trunk);
      // Leaves
      const leafMat = new THREE.MeshLambertMaterial({ color: 0x388e3c });
      for (let i = 0; i < 5; i++) {
        const leaf = new THREE.Mesh(new THREE.SphereGeometry(0.13, 8, 8), leafMat);
        leaf.position.set(
          x + Math.cos((i / 5) * Math.PI * 2) * 0.18,
          h + 0.08,
          z + Math.sin((i / 5) * Math.PI * 2) * 0.18
        );
        scene.add(leaf);
      }
    }
    addPalm(-1.1, 0.8, 1.1);
    addPalm(1.2, -0.7, 1.3);

    // Character group
    const character = new THREE.Group();
    scene.add(character);

    // Capsule body (cyan)
    const bodyColor = 0x00bcd4;
    const bodyMat = new THREE.MeshToonMaterial({ color: bodyColor });
    // Capsule: cylinder + two half-spheres
    const capsuleHeight = 1.1;
    const capsuleRadius = 0.32;
    const bodyCylinder = new THREE.Mesh(
      new THREE.CylinderGeometry(capsuleRadius, capsuleRadius, capsuleHeight, 32),
      bodyMat
    );
    bodyCylinder.position.y = capsuleHeight / 2 + 0.18;
    character.add(bodyCylinder);

    // Top hemisphere (head, not separated)
    const headSphere = new THREE.Mesh(
      new THREE.SphereGeometry(capsuleRadius, 32, 16, 0, Math.PI * 2, 0, Math.PI / 2),
      bodyMat
    );
    headSphere.position.y = capsuleHeight + 0.18;
    character.add(headSphere);

    // Bottom hemisphere
    const bottomSphere = new THREE.Mesh(
      new THREE.SphereGeometry(capsuleRadius, 32, 16, 0, Math.PI * 2, Math.PI / 2, Math.PI / 2),
      bodyMat
    );
    bottomSphere.position.y = 0.18;
    character.add(bottomSphere);

    // Belt (purple)
    const beltMat = new THREE.MeshToonMaterial({ color: 0xb39ddb });
    const belt = new THREE.Mesh(
      new THREE.TorusGeometry(capsuleRadius + 0.03, 0.045, 16, 32),
      beltMat
    );
    belt.position.y = capsuleHeight * 0.45 + 0.18;
    belt.rotation.x = Math.PI / 2;
    character.add(belt);

    // Hat (purple)
    const hatMat = new THREE.MeshToonMaterial({ color: 0xb39ddb });
    const hatBrim = new THREE.Mesh(
      new THREE.CylinderGeometry(capsuleRadius * 1.05, capsuleRadius * 1.05, 0.07, 24),
      hatMat
    );
    hatBrim.position.y = capsuleHeight + 0.18 + capsuleRadius - 0.03;
    character.add(hatBrim);

    const hatTop = new THREE.Mesh(
      new THREE.CylinderGeometry(capsuleRadius * 0.7, capsuleRadius * 0.7, 0.18, 24),
      hatMat
    );
    hatTop.position.y = capsuleHeight + 0.18 + capsuleRadius + 0.07;
    character.add(hatTop);

    // Eyes (black)
    const eyeMat = new THREE.MeshToonMaterial({ color: 0x222222 });
    const leftEye = new THREE.Mesh(new THREE.SphereGeometry(0.045, 12, 12), eyeMat);
    leftEye.position.set(-0.09, capsuleHeight + 0.32, capsuleRadius * 0.92);
    character.add(leftEye);
    const rightEye = leftEye.clone();
    rightEye.position.x = 0.09;
    character.add(rightEye);

    // Mouth (smile, arc)
    const mouthShape = new THREE.BufferGeometry();
    const mouthRadius = 0.09;
    const mouthCurve = [];
    for (let i = 0; i <= 16; i++) {
      const a = Math.PI * (0.25 + i / 32);
      mouthCurve.push(
        new THREE.Vector3(
          Math.cos(a) * mouthRadius,
          capsuleHeight + 0.23 - Math.sin(a) * 0.04,
          capsuleRadius * 0.98 + 0.001
        )
      );
    }
    mouthShape.setFromPoints(mouthCurve);
    const mouthMat = new THREE.LineBasicMaterial({ color: 0x222222, linewidth: 3 });
    const mouth = new THREE.Line(mouthShape, mouthMat);
    character.add(mouth);

    // Arms (thin, round)
    function addArm(side = 1) {
      const armMat = new THREE.MeshToonMaterial({ color: bodyColor });
      const arm = new THREE.Mesh(
        new THREE.CylinderGeometry(0.045, 0.045, 0.55, 16),
        armMat
      );
      arm.position.set(side * (capsuleRadius + 0.07), capsuleHeight * 0.7, 0);
      arm.rotation.z = side * Math.PI / 6;
      character.add(arm);

      // Hand
      const hand = new THREE.Mesh(new THREE.SphereGeometry(0.06, 16, 16), armMat);
      hand.position.set(side * (capsuleRadius + 0.07 + 0.28), capsuleHeight * 0.7 - 0.22, 0);
      character.add(hand);
    }
    addArm(1);
    addArm(-1);

    // Legs (thin, round)
    function addLeg(side = 1) {
      const legMat = new THREE.MeshToonMaterial({ color: bodyColor });
      const leg = new THREE.Mesh(
        new THREE.CylinderGeometry(0.055, 0.055, 0.48, 16),
        legMat
      );
      leg.position.set(side * 0.13, 0.18 + 0.24, 0);
      leg.rotation.x = Math.PI / 32;
      character.add(leg);

      // Foot
      const foot = new THREE.Mesh(new THREE.SphereGeometry(0.07, 16, 16), legMat);
      foot.position.set(side * 0.13, 0.18, 0.09);
      character.add(foot);
    }
    addLeg(1);
    addLeg(-1);

    // Animation: bounce
    const clock = new THREE.Clock();
    let reqId;
    function animate() {
      const t = clock.getElapsedTime();
      character.position.y = Math.sin(t * 2.2) * 0.08;
      character.rotation.y = Math.sin(t * 0.7) * 0.08;
      renderer.render(scene, camera);
      reqId = requestAnimationFrame(animate);
    }
    animate();

    // Responsive resize
    function handleResize() {
      const w = mount.clientWidth || 320;
      const h = mount.clientHeight || 320;
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
      renderer.setSize(w, h);
    }
    window.addEventListener("resize", handleResize);

    // Cleanup
    return () => {
      cancelAnimationFrame(reqId);
      window.removeEventListener("resize", handleResize);
      renderer.dispose();
      if (mount && renderer.domElement) mount.removeChild(renderer.domElement);
      scene.traverse((obj) => {
        if (obj.isMesh) {
          if (obj.geometry) obj.geometry.dispose();
          if (obj.material) {
            if (Array.isArray(obj.material)) obj.material.forEach((mat) => mat.dispose());
            else obj.material.dispose();
          }
        }
      });
    };
  }, []);

  return <div ref={mountRef} style={{ width: "320px", height: "320px", borderRadius: "24px", boxShadow: "0 8px 32px #b39ddb33", background: "linear-gradient(135deg,#b3e5fc 0%,#fffde7 100%)" }} />;
}
