import React, { useRef, useEffect } from "react";
import * as THREE from "three";

export default function ThreePerson() {
  const mountRef = useRef(null);

  useEffect(() => {
    const mount = mountRef.current;
    const width = mount.clientWidth || 320;
    const height = mount.clientHeight || 320;

    // Scene & Camera
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf7f7fa);

    const camera = new THREE.PerspectiveCamera(45, width / height, 0.1, 100);
    camera.position.set(0, 1.2, 3.2);
    camera.lookAt(0, 1.0, 0);

    // Renderer
    const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: false });
    renderer.setSize(width, height);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    renderer.outputColorSpace = THREE.SRGBColorSpace;
    mount.appendChild(renderer.domElement);

    // Character group
    const character = new THREE.Group();
    scene.add(character);

    // Colors
    const bodyColor = 0x1de9b6; // turquoise
    const capColor = 0xb39ddb;  // soft purple
    const faceColor = 0xf3eaff; // light face
    const blushColor = 0xf8bbd0; // blush
    const pantColor = 0x3949ab; // blue pants
    const bootColor = 0x1de9b6; // turquoise boots
    const bootTrimColor = 0xb39ddb; // purple trim
    const beltColor = 0x1de9b6;
    const beltAccentColor = 0xb39ddb;
    const eyeColor = 0x222222;

    // Torso (rounded cylinder, jelly shape)
    const torsoMat = new THREE.MeshStandardMaterial({ color: bodyColor, flatShading: true });
    const torso = new THREE.Mesh(
      new THREE.CylinderGeometry(0.34, 0.28, 1.05, 16),
      torsoMat
    );
    torso.position.y = 0.7;
    character.add(torso);

    // Torso top (rounded)
    const torsoTop = new THREE.Mesh(
      new THREE.SphereGeometry(0.34, 16, 12, 0, Math.PI * 2, 0, Math.PI / 2),
      torsoMat
    );
    torsoTop.position.y = 1.225;
    character.add(torsoTop);

    // Torso bottom (rounded)
    const torsoBottom = new THREE.Mesh(
      new THREE.SphereGeometry(0.28, 16, 12, 0, Math.PI * 2, Math.PI / 2, Math.PI / 2),
      torsoMat
    );
    torsoBottom.position.y = 0.175;
    character.add(torsoBottom);

    // Head (sphere, face color)
    const headMat = new THREE.MeshStandardMaterial({ color: faceColor, flatShading: true });
    const head = new THREE.Mesh(
      new THREE.SphereGeometry(0.28, 16, 16),
      headMat
    );
    head.position.y = 1.55;
    character.add(head);

    // Cap (soft purple, stylized)
    const capMat = new THREE.MeshStandardMaterial({ color: capColor, flatShading: true });
    const cap = new THREE.Mesh(
      new THREE.SphereGeometry(0.31, 16, 12, 0, Math.PI * 2, 0, Math.PI / 2),
      capMat
    );
    cap.position.y = 1.67;
    character.add(cap);

    // Cap brim (thicker rim)
    const brim = new THREE.Mesh(
      new THREE.TorusGeometry(0.31, 0.045, 16, 32),
      capMat
    );
    brim.position.y = 1.62;
    brim.rotation.x = Math.PI / 2;
    character.add(brim);

    // Face blush (left/right)
    const blushMat = new THREE.MeshStandardMaterial({ color: blushColor, flatShading: true });
    const blushL = new THREE.Mesh(new THREE.SphereGeometry(0.045, 8, 8), blushMat);
    blushL.position.set(-0.11, 1.51, 0.22);
    character.add(blushL);
    const blushR = blushL.clone();
    blushR.position.x = 0.11;
    character.add(blushR);

    // Eyes (cartoon, vertical oval)
    const eyeMat = new THREE.MeshStandardMaterial({ color: eyeColor, flatShading: true });
    const leftEye = new THREE.Mesh(new THREE.SphereGeometry(0.035, 8, 8), eyeMat);
    leftEye.scale.y = 1.5;
    leftEye.position.set(-0.07, 1.57, 0.25);
    character.add(leftEye);
    const rightEye = leftEye.clone();
    rightEye.position.x = 0.07;
    character.add(rightEye);

    // Eyelashes (simple lines)
    function addLash(x, y, z, angle = 0.3) {
      const geo = new THREE.BufferGeometry().setFromPoints([
        new THREE.Vector3(0, 0, 0),
        new THREE.Vector3(Math.cos(angle) * 0.04, Math.sin(angle) * 0.04, 0)
      ]);
      const mat = new THREE.LineBasicMaterial({ color: eyeColor });
      const lash = new THREE.Line(geo, mat);
      lash.position.set(x, y, z);
      character.add(lash);
    }
    addLash(-0.07, 1.60, 0.25, 0.7);
    addLash(-0.07, 1.60, 0.25, 1.2);
    addLash(0.07, 1.60, 0.25, 2.44);
    addLash(0.07, 1.60, 0.25, 2.0);

    // Smiling mouth (arc)
    const mouthShape = new THREE.BufferGeometry();
    const mouthRadius = 0.07;
    const mouthCurve = [];
    for (let i = 0; i <= 16; i++) {
      const a = Math.PI * (0.25 + i / 32);
      mouthCurve.push(
        new THREE.Vector3(
          Math.cos(a) * mouthRadius,
          1.52 - Math.sin(a) * 0.03,
          0.255
        )
      );
    }
    mouthShape.setFromPoints(mouthCurve);
    const mouthMat = new THREE.LineBasicMaterial({ color: eyeColor, linewidth: 2 });
    const mouth = new THREE.Line(mouthShape, mouthMat);
    character.add(mouth);

    // Arms (longer, apart from body, relaxed pose)
    const armMat = new THREE.MeshStandardMaterial({ color: bodyColor, flatShading: true });
    const arms = [];
    function addArm(side = 1) {
      const arm = new THREE.Mesh(
        new THREE.CylinderGeometry(0.09, 0.09, 0.52, 12),
        armMat
      );
      arm.position.set(side * 0.38, 1.0, 0);
      arm.rotation.z = side * Math.PI / 7;
      character.add(arm);
      arms[side === 1 ? 0 : 1] = arm; // 0: right, 1: left

      // Hand (rounded)
      const hand = new THREE.Mesh(
        new THREE.SphereGeometry(0.11, 12, 12),
        armMat
      );
      hand.position.set(side * 0.38, 0.74, 0);
      character.add(hand);
    }
    addArm(1);
    addArm(-1);

    // Belt (turquoise, accent purple)
    const beltMat = new THREE.MeshStandardMaterial({ color: beltColor, flatShading: true });
    const belt = new THREE.Mesh(
      new THREE.TorusGeometry(0.24, 0.03, 16, 32),
      beltMat
    );
    belt.position.y = 0.45;
    belt.rotation.x = Math.PI / 2;
    character.add(belt);

    // Belt accent (small purple knot)
    const beltAccentMat = new THREE.MeshStandardMaterial({ color: beltAccentColor, flatShading: true });
    const beltAccent = new THREE.Mesh(
      new THREE.SphereGeometry(0.04, 8, 8),
      beltAccentMat
    );
    beltAccent.position.set(0.18, 0.45, 0.08);
    character.add(beltAccent);

    // Pants (blue, short)
    const pantMat = new THREE.MeshStandardMaterial({ color: pantColor, flatShading: true });
    const pants = new THREE.Mesh(
      new THREE.CylinderGeometry(0.22, 0.22, 0.32, 16),
      pantMat
    );
    pants.position.y = 0.12;
    character.add(pants);

    // Legs (blue, slightly apart)
    function addLeg(side = 1) {
      const leg = new THREE.Mesh(
        new THREE.CylinderGeometry(0.10, 0.10, 0.32, 12),
        pantMat
      );
      leg.position.set(side * 0.13, -0.04, 0);
      character.add(leg);

      // Boots (turquoise, purple trim)
      const bootMat = new THREE.MeshStandardMaterial({ color: bootColor, flatShading: true });
      const boot = new THREE.Mesh(
        new THREE.CylinderGeometry(0.12, 0.13, 0.16, 12),
        bootMat
      );
      boot.position.set(side * 0.13, -0.20, 0.06);
      character.add(boot);

      // Boot trim
      const trimMat = new THREE.MeshStandardMaterial({ color: bootTrimColor, flatShading: true });
      const trim = new THREE.Mesh(
        new THREE.TorusGeometry(0.13, 0.02, 12, 16),
        trimMat
      );
      trim.position.set(side * 0.13, -0.12, 0.06);
      trim.rotation.x = Math.PI / 2;
      character.add(trim);
    }
    addLeg(1);
    addLeg(-1);

    // Lighting
    const ambient = new THREE.AmbientLight(0xffffff, 0.85);
    scene.add(ambient);

    const dir = new THREE.DirectionalLight(0xffffff, 0.7);
    dir.position.set(2, 4, 2);
    scene.add(dir);

    // Animation: subtle bounce
    const clock = new THREE.Clock();
    let reqId;
    function animate() {
      const t = clock.getElapsedTime();
      character.position.y = Math.sin(t * 2.2) * 0.06;
      character.rotation.y = Math.sin(t * 0.7) * 0.10;

      // Waving right arm (arms[0])
      if (arms[0]) {
        arms[0].rotation.z = Math.PI / 7 + Math.sin(t * 2.5) * 0.7;
      }
      // Left arm fixed
      if (arms[1]) {
        arms[1].rotation.z = -Math.PI / 7;
      }

      renderer.render(scene, camera);
      reqId = requestAnimationFrame(animate);
    }
    animate();

    // Responsive resize
    function handleResize() {
      const w = mount.clientWidth || 320;
      const h = mount.clientHeight || 320;
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
      renderer.setSize(w, h);
    }
    window.addEventListener("resize", handleResize);

    // Cleanup
    return () => {
      cancelAnimationFrame(reqId);
      window.removeEventListener("resize", handleResize);
      renderer.dispose();
      if (mount && renderer.domElement) mount.removeChild(renderer.domElement);
      scene.traverse((obj) => {
        if (obj.isMesh) {
          if (obj.geometry) obj.geometry.dispose();
          if (obj.material) {
            if (Array.isArray(obj.material)) obj.material.forEach((mat) => mat.dispose());
            else obj.material.dispose();
          }
        }
      });
    };
  }, []);

  return (
    <div
      ref={mountRef}
      style={{
        width: "320px",
        height: "320px",
        borderRadius: "24px",
        boxShadow: "0 8px 32px #90caf933",
        background: "#f7f7fa",
        transition: "box-shadow 0.3s",
      }}
    />
  );
}
